import React from 'react';
import PropTypes from 'prop-types';
import Link from 'next/link';
import { Menu, Input, Row, Col } from 'antd';
import styled from 'styled-components';
import {useSelector} from 'react-redux';
import { createGlobalStyle } from 'styled-components';

import UserProfile from '../components/UserProfile';
import LoginForm from '../components/LoginForm';


const Global = createGlobalStyle`
    .ant-row {
        margin-right: 0 !import;
        margin-left: 0 !import;
    }

    .ant-col:first-child {
        padding-left: 0 !import;
    }

    .ant-col:last-child {
        padding-right: 0 !import; 
    }
`;

const SearchInput = styled(Input.Search)`
    vertical-align: middle;
`;

const AppLayout = ({children}) => {
    const { me } = useSelector((state) => state.user);

    return (
        <div>
            <Global />
            <Menu mode="horizontal">
                <Menu.Item>
                <img style={{width: '50px', height: '50px'}} src="https://velog.velcdn.com/images/onezerolee00/post/a43beec0-e69c-4da9-b98e-b0b13f982af0/image.png" />
                </Menu.Item>
                <Menu.Item>
                <Link href="/"><a>노드버드</a></Link>
                </Menu.Item>
                <Menu.Item>
                <Link href="/profile"><a>프로필</a></Link>
                </Menu.Item>
                <Menu.Item>
                <SearchInput enterButton />
                </Menu.Item>
                <Menu.Item>
                <Link href="/signup"><a>회원가입</a></Link>
                </Menu.Item>
            </Menu>
            <Row gutter={8}>
                <Col xs={24} md={6}>
                    {me ? <UserProfile /> : <LoginForm />}
                </Col>
                <Col xs={24} md={12}>{children}</Col>
                {/* <Col xs={24} md={6}><a href='https://www.zerocho.com' target="_blank" rel="noreferrer noopner">Made by ZeroCho</a></Col> */}
            </Row>
        </div>
    )
};

AppLayout.propTypes = {
    children: PropTypes.node.isRequired,
};

export default AppLayout;
