import PropTypes from 'prop-types';
import { List, Button, Avatar } from 'antd';

const FollowList = ({data, header}) => {
    const dispatch = useDispatch();
    const { me, followLoading, unfollowLoading } = useSelector((state) => state.user); // 리덕스에서 내 정보 받아오고
    const isFollowing = me?.Followings.find((v) => v.id === data.User.id) // 포스트 작성한 사람들 중에 내가 팔로윙한 사람들

    const onClickButton = useCallback(() => {
        if (isFollowing) { // 내가 팔로잉을 하고 있는데 버튼을 눌렀다
            dispatch({
                type: UNFOLLOW_REQUEST,
                data: post.User.id,
            });
        } else {
            dispatch({
                type: FOLLOW_REQUEST,
                data: post.User.id,
            });
        }
    }, [isFollowing]);

    return (
        <List 
            bordered
            itemLayout="horizontal"
            dataSource={data}
            renderItem={(item) => (
                <List.Item>
                    <List.Item.Meta 
                    avatar={<Avatar>{item.nickname[0]}</Avatar>}
                    title={item.nickname}
                    />
                    <Button loading={followLoading || unfollowLoading} onClick={onClickButton}>
                    {isFollowing ? '언팔로우' : '팔로우'}
                    </Button>
                </List.Item>
            )}
        />
    )
