            });
        } else {
            dispatch({
                type: FOLLOW_REQUEST,
                data: post.User.id,
            });
        }
    }, [isFollowing]);

    return (
        <List 
            bordered
            itemLayout="horizontal"
            dataSource={data}
            renderItem={(item) => (
                <List.Item>
                    <List.Item.Meta 
                    avatar={<Avatar>{item.nickname[0]}</Avatar>}
                    title={item.nickname}
                    />
                    <Button loading={followLoading || unfollowLoading} onClick={onClickButton}>
                    {isFollowing ? '언팔로우' : '팔로우'}
                    </Button>
                </List.Item>
            )}
        />
    )
