import React, {useCallback} from 'react';
import { Avatar, Card, Button, Menu } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import { TeamOutlined } from '@ant-design/icons';

import { LOG_OUT_REQUEST } from '../reducers/user';

const UserProfile = () => {
    const dispatch = useDispatch();
    const { me, logOutLoading } = useSelector((state) => state.user);

    const onLogOut = useCallback(() => {
        dispatch({
            type: LOG_OUT_REQUEST,
        });
    }, []);
    return (
        <Card>
            <Card.Meta 
                avatar={<Avatar>{me.nickname[0]}</Avatar>}
                title={me.nickname}/>
                <Button onClick={onLogOut} loading={logOutLoading}>로그아웃</Button>
            <Menu theme='white' mode="inline">
                <Menu.Item><TeamOutlined key="follwings"/>팔로잉 {me.Followings.length}</Menu.Item>
            </Menu>
        </Card>
        // <Card
        //     actions={[
        //         <div key="twit">짹짹<br />{me.Posts.length}</div>,
        //         <div key="follwings">팔로잉<br />{me.Followings.length}</div>,
        //         <div key="follwings">팔로워<br />{me.Followers.length}</div>,
        //     ]}>
        //     <Card.Meta 
        //         avatar={<Avatar>{me.nickname[0]}</Avatar>}
        //         title={me.nickname}/>
        //         <Button onClick={onLogOut} loading={logOutLoading}>로그아웃</Button>
        // </Card>
    );
};

export default UserProfile;
